mapper.select:

select #{user_id}, #{item_code},#{item_seq}, #{item_category1}, #{item_category2}, #{item_category3}, 
#{item_name}, #{item_image1}, #{item_details}, #{item_size}, #{item_color}, #{item_price}, #{item_date} 
from (select rownum rn, tt. * from
(select * from itemView order by item_seq desc)tt)
where rn>=#{startNum} and rn<=#{endNum}
